import React, { useContext, useEffect, Fragment } from 'react';

import { BoardActions } from '../actions/BoardActions';
import { dispatchContext, stateContext } from '../Game';

import './Cell.scss';

export function Cell(props) {
  const dispatch = useContext(dispatchContext);
  const { gameOver, level, clickNumber } = useContext(stateContext);
  let { hasMine, isOpen, countMinesAround, row, column, hasFlag } = props.cellInfo;


  useEffect(() => {
    if (isOpen && hasMine) dispatch({ type: BoardActions.GAME_OVER });
  }, [isOpen]) // ojooooooooooo , dispatch, hasMine

  function handleCellClick() {
    if (hasFlag || gameOver) return;
    if (clickNumber === 0) {
      dispatch({ type: BoardActions.INITIATE_BOARD, payload: { level, row, column } });
    }
    dispatch({ type: BoardActions.UNCOVER_CELL, payload: { row, column } });
  }

  function onCellDoubleClick() {
    if (gameOver) return;
    dispatch({ type: BoardActions.DOUBLE_CLICK_CELL, payload: { row, column } })
  }

  function handleCellRightClick(e) {
    e.preventDefault();
    e.stopPropagation();
    if (gameOver || isOpen) return;
    dispatch({ type: BoardActions.RIGHT_CLICK_CELL, payload: { row, column } });
    return false;
  }

  return (
    <Fragment>
      <div
        className={`cell ${isOpen ? 'open' : ''} ${hasFlag ? 'noHover' : ''}`}
        onClick={handleCellClick}
        onDoubleClick={onCellDoubleClick}
        onContextMenu={(e) => handleCellRightClick(e)}
      >
        <div
          className={`
          cell_content _${countMinesAround} 
          ${(hasMine && isOpen) || (hasMine && gameOver) ? 'mine' : ''} 
          ${hasFlag ? 'hasFlag' : ''}`}
        >
          {(countMinesAround && !hasMine && isOpen) ? countMinesAround : ''}
        </div>
      </div>
    </Fragment>

  );
}
