import React, { useReducer } from 'react';

import '@fortawesome/fontawesome-free/js/all.js';
import './Game.scss';

import { Board } from './components/Board';

import { boardReducer } from './reducers/boardReducer';
import { BoardActions } from './actions/BoardActions';
import { CurrentLevel, InitialState } from './constants/gameConstants';

export const stateContext = React.createContext();
export const dispatchContext = React.createContext();


function Game() {
  const [state, dispatch] = useReducer(boardReducer, InitialState);

  function handleReset() {
    dispatch({
      type: BoardActions.INITIATE_DUMMY_BOARD,
      payload: {
        level: state.level
      }
    });
  }

  function handleLevel(e) {
    dispatch({
      type: BoardActions.CHANGE_LEVEL,
      payload: {
        level: CurrentLevel[e.target.value]
      }
    });
  }

  return (
    <stateContext.Provider value={state}>
      <dispatchContext.Provider value={dispatch}>
        <h1 className="title1" >LET START!!</h1>
        <div className="game_container">
          {state.gameClear ?
            <i className="gameClear">YOU ROCK!!!</i> : ''}
          <div className="game">
            <div className="game_header">
              <div className="game_minesLeft">Mines: {state.minesLeft}</div>
              <button
                className="restart"
                onClick={handleReset}>Restart</button>
              <select
                className="levelSelector"
                value={state.level.name}
                onChange={(e) => handleLevel(e)}>
                <option value={CurrentLevel.EASY.name}>Easy</option>
                <option value={CurrentLevel.INTERMEDIATE.name}>Intermediate</option>
                <option value={CurrentLevel.EXPERT.name}>Expert</option>
              </select>
            </div>
            <Board />
          </div>
          {/* <div className="credentials">
            <a className="github"
               href="https://github.com/RebeccaPark/minesweeper">
              <div className="fab fa-github"/>
            </a>
            <a className="twitter"
               href="https://twitter.com/RebeccaJYPark">
              <div className="fab fa-twitter"/>
            </a>
          </div> */}
        </div>
      </dispatchContext.Provider>
    </stateContext.Provider>
  );
}

export default Game;
