import { InjectMineEnum } from '../constants/gameConstants';
//import { BoardActions } from '../actions/BoardActions';
import actionsName from "../actions/actionsName"
import { referenceToAdjacentCells, getSurroundingCellIndex } from '../utility/utility';

export function boardReducer(state, { type, payload }) {
  switch (type) {
    case actionsName.CREATE_BLANK_BOARD:
      return {
        ...state,
        gameOver: false,
        winner: false,
        isStarted: false,
        hiddenMines: state.level.totalMines,
        board: setBlankBoard(state.level),
      };

    case actionsName.CREATE_STARTED_GAME_BOARD:
      return {
        ...state,
        hiddenMines: payload.level.totalMines,
        board: seedMinesAndNumbers(state.board, payload.level, payload.row, payload.column),
        isStarted: true,
      };

    case actionsName.UNCOVER_CELL:
      return {
        ...state,
        board: uncoverCell(state.board, payload.row, payload.column),
        winner: didWin(uncoverCell(state.board, payload.row, payload.column)),
      };

    // case actionsName.DOUBLE_CLICK_CELL:
    //   return {
    //     ...state,
    //     board: uncoverAdjacentCells(state.board, payload.row, payload.column),
    //     winner: didWin(uncoverAdjacentCells(state.board, payload.row, payload.column)),
    //   };

    case actionsName.RIGHT_CLICK_CELL:
      return {
        ...state,
        hiddenMines: hiddenMines(state.board, payload.row, payload.column, state.hiddenMines),
        board: toggleFlagCell(state.board, payload.row, payload.column),
        winner: didWin(toggleFlagCell(state.board, payload.row, payload.column)),
      };

    case actionsName.CHANGE_LEVEL:
      return {
        ...state,
        gameOver: false,
        winner: false,
        isStarted: false,
        level: payload.level,
        hiddenMines: payload.level.totalMines,
        board: setBlankBoard(payload.level),
      };

    case actionsName.GAME_OVER:
      return {
        ...state,
        gameOver: true,
      };

    default:
      throw new Error('No matching action type in reducer');
  }
}



















function setBlankBoard({ rows, columns }) {
  return Array.from(
    Array(rows), () =>
    new Array(columns).fill({
      hasMine: false,
      isOpen: false,
      countMinesAround: 0,
      hasFlag: false,
    })
  );
}

function seedMinesAndNumbers(board, level, row, column) {
  let boardWithSeedMines = seedMine(board, level.totalMines, row, column);
  return seedNumber(boardWithSeedMines, row, column);
}

function seedMine(blankBoard, numMineToSeed, row, column) {
  let totalSeedMines = 0; //numInjectedMines
  let tempBoard = JSON.parse(JSON.stringify(blankBoard));

  let rowIndex = 0;

  const indexAroundClickedCell = getSurroundingCellIndex(row, column).map(index => JSON.stringify(index));

  while (totalSeedMines < numMineToSeed) {
    for (let i = 0; i < blankBoard[rowIndex].length; i++) {
      console.log("blankBoard[rowIndex]", blankBoard[rowIndex])
      if (totalSeedMines === numMineToSeed) break;


      if (rowIndex === row && i === column) {
        continue;
      }

      if (indexAroundClickedCell.includes(JSON.stringify([rowIndex, i]))) {
        continue;
      }

      const luckyNumber = Math.floor(Math.random() * Math.floor(10));
      // Tip: the mine would be seed only when the lucky number is 1
      console.log("numero", luckyNumber)

      if (luckyNumber === 1 && tempBoard[rowIndex][i].hasMine === false) {
        tempBoard[rowIndex][i].hasMine = true;
        totalSeedMines++;
      }
    }

    if (rowIndex === blankBoard.length - 1) {
      rowIndex = 0;
    } else {
      rowIndex++;
    }
  }
  return tempBoard;
}

function seedNumber(prevBoard) {
  for (let i = 0; i < prevBoard.length; i++) {
    for (let j = 0; j < prevBoard[i].length; j++) {
      const adjacentCells = referenceToAdjacentCells(prevBoard, i, j);
      let minesAround = 0;
      adjacentCells.forEach(({ cell, row, column }) => {
        if (cell.hasMine) minesAround++;
      })

      prevBoard[i][j].countMinesAround = minesAround;
    }
  }
  return prevBoard;
};



function hiddenMines(board, row, column, hiddenMines) {
  return board[row][column].hasFlag ? hiddenMines + 1 : hiddenMines - 1
}

function uncoverCell(originalBoard, row, column) {
  const newBoard = JSON.parse(JSON.stringify(originalBoard));
  newBoard[row][column].isOpen = true;
  if (newBoard[row][column].countMinesAround === 0 && !newBoard[row][column].hasMine) {
    return uncoverAdjacentCells(newBoard, row, column);
  }
  return newBoard;
}

function toggleFlagCell(originalBoard, row, column) {
  const newBoard = JSON.parse(JSON.stringify(originalBoard));
  newBoard[row][column].hasFlag = !newBoard[row][column].hasFlag;
  return newBoard
}

function uncoverAdjacentCells(originalBoard, row, column) {

  const memo = {};
  const stack = [{ cell: originalBoard[row][column], row, column }];

  while (stack.length > 0) {
    const centerCell = stack.pop();
    const surroundingCells = referenceToAdjacentCells(originalBoard, centerCell.row, centerCell.column);

    const flagsInAdjacentCells = surroundingCells.reduce((acc, currCell) => {
      if (currCell.cell.hasFlag) return acc + 1;
      return acc;
    }, 0);

    if (flagsInAdjacentCells < centerCell.cell.countMinesAround) return originalBoard;

    for (let cell of surroundingCells) {
      if (memo[`${cell.row}-${cell.column}`]) continue;
      else {
        memo[`${cell.row}-${cell.column}`] = true;
      }

      cell.cell.isOpen = cell.cell.hasFlag ? false : true;

      if (cell.cell && !cell.cell.countMinesAround && !cell.cell.hasMine) {
        stack.push(cell);
      }
    }
  }

  return originalBoard;
};

function didWin(board) {
  for (let i = 0; i < board.length; i++) {
    for (let j = 0; j < board[0].length; j++) {
      if (!board[i][j].hasMine && !board[i][j].isOpen) {
        return false;
      }

      if (!board[i][j].hasMine && board[i][j].hasFlag) {
        return false;
      }
    }
  }
  return true;
}
